public class MusicLibraryClient {
    public static void main(String[] args) {
        MusicLibrary myLibrary = new MusicLibrary();

        Album a1 = new Album("First", "one Guy", 10, 42.5);
        Album a2 = new Album("This Music", "Some Band", 9, 36);
        Album a3 = new Album("Play", "People", 12, 39.1);
        Album a4 = new Album("Colores", "J Balvin", 3, 12.5); 

        myLibrary.add(a1);
        myLibrary.add(a2);
        myLibrary.add(a4);
        myLibrary.add(a3);

        System.out.println(myLibrary);

        System.out.println("");
        myLibrary.doubleSize();
        
        myLibrary.sortByTitle();
        
        System.out.println(myLibrary);
        
        System.out.println("");


        System.out.println(myLibrary);

        System.out.println("");

        myLibrary.remove(1);
        myLibrary.add(a3);
        System.out.println(myLibrary);

        System.out.println("");
       
        System.out.println(myLibrary.findAlbum("Colores"));
        System.out.println(myLibrary.findArtist("Guy"));
        
        
        myLibrary.sortByArtist();
        
        System.out.println(myLibrary);

        System.out.println("");
       
        
        int binaryArtistLoc = myLibrary.binaryFindArtist("J Balvin");
        System.out.println(myLibrary.getAlbum(binaryArtistLoc));

        int binaryTitleLoc = myLibrary.binaryFindTitle("Colores");
        System.out.println(myLibrary.getAlbum(binaryTitleLoc));
        
        
    }
    
}